package STRING;
class PatternSearchingBenchmark {

    public static void main(String[] args) {
        String text = "";
        String pattern ="a";

        long startTime = System.nanoTime();
        boolean naiveResult = naivePatternSearch(text, pattern);
        long naiveTimeNano = System.nanoTime() - startTime;
        double naiveTimeMs = naiveTimeNano / 1_000_000.0; // Convert to milliseconds
        System.out.println("Naive Result: " + naiveResult + ", Time: " + String.format("%.2f", naiveTimeMs) + " ms");

        // Benchmark KMP Approach
        startTime = System.nanoTime();
        boolean kmpResult = kmpPatternSearch(text, pattern);
        long kmpTimeNano = System.nanoTime() - startTime;
        double kmpTimeMs = kmpTimeNano / 1_000_000.0; // Convert to milliseconds
        System.out.println("KMP Result: " + kmpResult + ", Time: " + String.format("%.2f", kmpTimeMs) + " ms");

        // Benchmark Rabin-Karp Approach
        startTime = System.nanoTime();
        boolean rabinKarpResult = rabinKarpPatternSearch(text, pattern);
        long rabinKarpTimeNano = System.nanoTime() - startTime;
        double rabinKarpTimeMs = rabinKarpTimeNano / 1_000_000.0; // Convert to milliseconds
        System.out.println("Rabin-Karp Result: " + rabinKarpResult + ", Time: " + String.format("%.2f", rabinKarpTimeMs) + " ms");

    }

    private static boolean naivePatternSearch(String text, String pattern) {
        int n = text.length();
        int m = pattern.length();

        for (int i = 0; i <= n - m; i++) {
            int j = 0;
            while (j < m && text.charAt(i + j) == pattern.charAt(j)) {
                j++;
            }
            if (j == m) {
                return true;
            }
        }
        return false;
    }

    private static boolean kmpPatternSearch(String text, String pattern) {
        int m = pattern.length();
        int n = text.length();
        int[] lps = computeLPSArray(pattern);

        int i = 0; // index for text
        int j = 0; // index for pattern

        while (i < n) {
            if (pattern.charAt(j) == text.charAt(i)) {
                i++;
                j++;
            }

            if (j == m) {
                return true; // Pattern found
            } else if (i < n && pattern.charAt(j) != text.charAt(i)) {
                if (j != 0) {
                    j = lps[j - 1];
                } else {
                    i++;
                }
            }
        }
        return false;
    }

    private static int[] computeLPSArray(String pattern) {
        int m = pattern.length();
        int[] lps = new int[m];
        int length = 0;
        int i = 1;

        lps[0] = 0;

        while (i < m) {
            if (pattern.charAt(i) == pattern.charAt(length)) {
                length++;
                lps[i] = length;
                i++;
            } else {
                if (length != 0) {
                    length = lps[length - 1];
                } else {
                    lps[i] = 0;
                    i++;
                }
            }
        }
        return lps;
    }

    private static boolean rabinKarpPatternSearch(String text, String pattern) {
        int m = pattern.length();
        int n = text.length();
        int prime = 101; // A prime number for hashing
        int patternHash = 0;
        int textHash = 0;
        int h = 1; // The hash factor for the rolling hash

        // Calculate h = pow(d, m-1) % q
        for (int i = 0; i < m - 1; i++) {
            h = (h * 256) % prime;
        }

        // Calculate hash value of pattern and first window of text
        for (int i = 0; i < m; i++) {
            patternHash = (256 * patternHash + pattern.charAt(i)) % prime;
            textHash = (256 * textHash + text.charAt(i)) % prime;
        }

        // Slide the pattern over text
        for (int i = 0; i <= n - m; i++) {
            if (patternHash == textHash) {
                boolean match = true;
                for (int j = 0; j < m; j++) {
                    if (text.charAt(i + j) != pattern.charAt(j)) {
                        match = false;
                        break;
                    }
                }
                if (match) return true;
            }
            if (i < n - m) {
                textHash = (256 * (textHash - text.charAt(i) * h) + text.charAt(i + m)) % prime;
                if (textHash < 0) textHash += prime;
            }
        }
        return false;
    }

    // Helper method to generate a large text string
    private static String generateLargeText(int size) {
        StringBuilder sb = new StringBuilder(size);
        for (int i = 0; i < size; i++) {
            sb.append((char) ('a' + (i % 26))); // Generate a repeating pattern of characters
        }
        return sb.toString();
    }
}
